import { Address, ContractProvider, Cell, SenderArguments } from '@ton/ton';
import { AmountType, AddressType, QueryIdType } from '../../types.js';
import { pTON_VERSION } from './constants.js';

interface AbstractPton {
    version: pTON_VERSION;
    address: Address;
    getTonTransferTxParams(provider: ContractProvider, params: {
        tonAmount: AmountType;
        destinationAddress: AddressType;
        refundAddress: AddressType;
        forwardPayload?: Cell;
        forwardTonAmount?: AmountType;
        queryId?: QueryIdType;
    }): Promise<SenderArguments>;
}

export type { AbstractPton };
